mapper.select:
SELECT * from t_sys_user u LEFT JOIN identity i ON (u.username = i.uname) LEFT JOIN personal p on (u.username = p.uname) where 1=1
and u.username LIKE concat('%',#{username},'%')
and date_format(u.createdate,'%Y-%m-%d') BETWEEN #{startDate} and #{endDate}

mapper.insert:
insert into t_sys_user (username, password,
  salt, tel
  )
values (#{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR},
  #{salt,jdbcType=VARCHAR}, #{tel,jdbcType=VARCHAR}
  )
